
// server.js
'use strict';

const express = require('express');
const mysql = require('mysql');
const bodyParser = require('body-parser');
require('dotenv').config();

const app = express();
const PORT = process.env.PORT || 5000;

// Middleware
app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: true }));

// MySQL Connection
const connection = mysql.createConnection({
  host: process.env.DB_HOST,
  user: process.env.DB_USER,
  password: process.env.DB_PASSWORD,
  database: process.env.DB_NAME
});

connection.connect((err) => {
  if (err) {
    console.error('Database connection failed: ' + err.stack);
    return;
  }
  console.log('Connected to database.');
});

// Routes
app.post('/api/users/signup', (req, res) => {
  const { firstName, lastName, age, phone, email, password } = req.body;

  // Insert the new user into the database
  const query = 'INSERT INTO users (first_name, last_name, age, phone, email, password) VALUES (?, ?, ?, ?, ?, ?)';
  connection.query(query, [firstName, lastName, age, phone, email, password], (err, results) => {
    if (err) {
      console.error(err);
      return res.status(500).send('Server error');
    }
    res.status(201).send('User registered successfully');
  });
});

// Start server
app.listen(PORT, () => {
  console.log(`Server is running on port ${PORT}`);
});
